mapper.select:
    SELECT  *
	FROM   TEST_MANAGEMENT	
	WHERE  TEST_ID = #{TEST_ID}

SELECT COUNT(*) CNT
FROM STEP_ACTION
WHERE 	PROJECT_ID =#{PROJECT_ID}
AND	  	TEST_ID	 =#{TEST_ID}
AND	  	SCENARIO_ID=#{SCENARIO_ID}
AND   	CASE_ID=#{CASE_ID}
AND	  	STEP_ID=#{STEP_ID}

AND	  	TESTER_ID	 =#{TESTER_ID}

SELECT COUNT(*) CNT
FROM STEP_ACTION
WHERE 	PROJECT_ID =#{PROJECT_ID}
AND	  	TEST_ID	 =#{TEST_ID}
AND	  	SCENARIO_ID=#{SCENARIO_ID}
AND   	CASE_ID=#{CASE_ID}
AND	  	STEP_ID=#{STEP_ID}
AND	  	DEL_FLAG='N'

AND	  	TESTER_ID	 =#{TESTER_ID}

SELECT COUNT(*) CNT 
FROM SCENARIO
WHERE  PROJECT_ID=#{PROJECT_ID} 
	AND TEST_ID = #{TEST_ID}

SELECT COUNT(*) CNT 
FROM SCENARIO
WHERE  PROJECT_ID=#{PROJECT_ID} 
	AND TEST_ID = #{TEST_ID}
	AND DEL_FLAG = 'N'

SELECT COUNT(*) CNT 
FROM CIRCUMSTANCE 
