mapper.update:
UPDATE TEST_MANAGEMENT
SET 
	TEST_NM = #{TEST_NM},
	TEST_STTG_DATE = #{TEST_STTG_DATE},
	TEST_ENDG_DATE= #{TEST_ENDG_DATE},
	TEST_BIGO=#{TEST_BIGO},
	DEL_FLAG=#{DEL_FLAG}
WHERE 
	PROJECT_ID=#{PROJECT_ID}		
AND	TEST_ID =#{TEST_ID}

UPDATE SCENARIO
SET

	SCENARIO_NM	=#{SCENARIO_NM},
	START_DATE	=#{START_DATE},
	END_DATE	=#{END_DATE},

	DEL_FLAG	=#{DEL_FLAG}	
WHERE
1=1

AND SCENARIO_ID	=#{SCENARIO_ID}
	
AND	PROJECT_ID	=#{PROJECT_ID}
AND	TEST_ID	  	=#{TEST_ID}

UPDATE CIRCUMSTANCE
SET

	CASE_NM		=#{CASE_NM},
	START_DATE	=#{START_DATE},
	END_DATE	=#{END_DATE},

	DEL_FLAG	=#{DEL_FLAG}	
WHERE
1 = 1 

	AND CASE_ID		=#{CASE_ID}


	AND SCENARIO_ID	=#{SCENARIO_ID}

	AND PROJECT_ID		=#{PROJECT_ID}
	AND	TEST_ID	  	=#{TEST_ID}

UPDATE STEP
SET
	
		STEP_NM		=#{STEP_NM},
		START_DATE	=#{START_DATE},
		END_DATE	=#{END_DATE},
	
		DEL_FLAG	=#{DEL_FLAG}	
WHERE
		1=1
		
		AND STEP_ID		=#{STEP_ID}		
		
		
		AND CASE_ID		=#{CASE_ID}
		
		
		AND SCENARIO_ID	=#{SCENARIO_ID}
		
		AND	PROJECT_ID	=#{PROJECT_ID}
		AND	TEST_ID	  	=#{TEST_ID}


	UPDATE	TEST_MANAGEMENT
	SET 	DEL_FLAG= 'Y' 
	WHERE 	PROJECT_ID=#{PROJECT_ID}		
	AND		TEST_ID =#{TEST_ID}

UPDATE SCENARIO
SET	   DEL_FLAG= 'Y'
WHERE  PROJECT_ID=#{PROJECT_ID}
AND    TEST_ID	 =#{TEST_ID}
AND	   SCENARIO_ID=#{SCENARIO_ID}

UPDATE CIRCUMSTANCE
SET	   DEL_FLAG= 'Y'
WHERE  PROJECT_ID =#{PROJECT_ID}
AND    TEST_ID	  =#{TEST_ID}
AND	   SCENARIO_ID=#{SCENARIO_ID}
AND    CASE_ID	  =#{CASE_ID}

UPDATE 	STEP
SET 	DEL_FLAG='Y'
WHERE  PROJECT_ID =#{PROJECT_ID}
AND    TEST_ID	  =#{TEST_ID}
AND	   SCENARIO_ID=#{SCENARIO_ID}
AND    CASE_ID	  =#{CASE_ID}
AND	   STEP_ID	  =#{STEP_ID}

mapper.select:
	SELECT	DISTINCT U.USERNAME,
			S.TESTER_ID
	FROM USERINFO  AS U, STEP_ACTION AS S
	WHERE U.USERID = S.TESTER_ID
	AND   PROJECT_ID =#{PROJECT_ID}
	AND	  TEST_ID	 =#{TEST_ID}
    AND	  SCENARIO_ID=#{SCENARIO_ID}

	SELECT	DISTINCT U.USERNAME,
			S.TESTER_ID
	FROM USERINFO  AS U,
		 STEP_ACTION AS S
	WHERE U.USERID = S.TESTER_ID
	AND   PROJECT_ID =#{PROJECT_ID}
	AND	  TEST_ID	 =#{TEST_ID}
    AND	  SCENARIO_ID=#{SCENARIO_ID}
    AND   CASE_ID=#{CASE_ID}

	SELECT	TESTER_ID,
			START_DATE,
			END_DATE,
			STEP_STATUS,
			STATUS_LEVEL,
			TEST_ID,
			SCENARIO_ID,
			CASE_ID,
			STEP_ID,
			ACTION_SEQ,
			DEL_FLAG
	FROM  	STEP_ACTION
	WHERE 	PROJECT_ID=#{PROJECT_ID}
	AND	  	TEST_ID=#{TEST_ID}
    AND	  	SCENARIO_ID=#{SCENARIO_ID}
    AND   	CASE_ID=#{CASE_ID}
    AND	  	STEP_ID=#{STEP_ID}
    AND	  	DEL_FLAG='N'
    
    AND	  	TESTER_ID=#{TESTER_ID}

	SELECT	TESTER_ID,
			START_DATE,
			END_DATE,
			STEP_STATUS,
			STATUS_LEVEL,
			TEST_ID,
			SCENARIO_ID,
			CASE_ID,
			STEP_ID,
			ACTION_SEQ,
			DEL_FLAG
	FROM  	STEP_ACTION
	WHERE 	PROJECT_ID=#{PROJECT_ID}
	AND	  	TEST_ID=#{TEST_ID}
    AND	  	SCENARIO_ID=#{SCENARIO_ID}
    AND   	CASE_ID=#{CASE_ID}
    AND	  	STEP_ID=#{STEP_ID}
    
    AND	  	TESTER_ID=#{TESTER_ID}

		SELECT A.STEP_STATUS,A.STATUS_LEVEL
        FROM STEP AS S, STEP_ACTION A
        WHERE S.STEP_ID = A.STEP_ID
	        AND S.PROJECT_ID =#{PROJECT_ID}
			AND	S.TEST_ID	 =#{TEST_ID}
		    AND	S.SCENARIO_ID=#{SCENARIO_ID}
		    AND S.CASE_ID=#{CASE_ID}
		    AND	S.STEP_ID=#{STEP_ID}

SELECT COUNT(DISTINCT SCENARIO_ID) AS CNT
FROM STEP_ACTION
WHERE TESTER_ID=#{TESTER_ID}
AND TEST_ID=#{TEST_ID}
AND DEL_FLAG='N'

SELECT COUNT(DISTINCT CASE_ID) AS CNT
FROM STEP_ACTION
WHERE TESTER_ID=#{TESTER_ID}
AND TEST_ID=#{TEST_ID}
AND DEL_FLAG='N'

SELECT COUNT(DISTINCT STEP_ID) AS CNT
FROM STEP_ACTION
WHERE TESTER_ID=#{TESTER_ID}
AND TEST_ID=#{TEST_ID}
AND DEL_FLAG='N'

mapper.update:
UPDATE 	STEP_ACTION
SET		STEP_STATUS=#{STEP_STATUS},	
		STATUS_LEVEL=#{STATUS_LEVEL},
		END_DATE=#{END_DATE}
WHERE	TEST_ID=#{TEST_ID}
AND		TESTER_ID=#{TESTER_ID}
AND		SCENARIO_ID=#{SCENARIO_ID}
AND		CASE_ID=#{CASE_ID}
AND		STEP_ID=#{STEP_ID}
AND		ACTION_SEQ=#{ACTION_SEQ}

UPDATE 	STEP_ACTION
SET		DEL_FLAG	='Y'
WHERE	TEST_ID		=#{TEST_ID}
AND 	TESTER_ID	=#{TESTER_ID}
AND 	SCENARIO_ID	=#{SCENARIO_ID}
AND 	CASE_ID		=#{CASE_ID} 		
AND 	STEP_ID		=#{STEP_ID}
AND 	DEL_FLAG	='N'
AND 	ACTION_SEQ	=#{ACTION_SEQ}

UPDATE 	STEP_ACTION
SET		DEL_FLAG	=#{DEL_FLAG}
WHERE	1=1
		
		AND STEP_ID		=#{STEP_ID}		
		
		
		AND CASE_ID		=#{CASE_ID}
		
		
		AND SCENARIO_ID	=#{SCENARIO_ID}
		
		
		AND 	ACTION_SEQ	=#{ACTION_SEQ}
		
AND		TEST_ID		=#{TEST_ID}
AND 	TESTER_ID	=#{TESTER_ID}
